import React, { useState } from 'react';
import Appbar from './Appbar.js'; 
import Product from './Product.js';
import Grid from '@mui/material/Grid2';
import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';
import Typography from '@mui/material/Typography';

export default function ProductPage() {
    const [isLoggedIn, setIsLoggedIn] = useState(true); 
    const userData = {
      shoppingCartItems: 3, 
    };
  
    // Dummy list of products
    const products = [
      { productName: 'Item 1', price: 19.99, imageUrl: '/media/product1.png' },
      { productName: 'Item 2', price: 29.99, imageUrl: '/media/product1.png' },
      { productName: 'Item 3', price: 39.99, imageUrl: '/media/product1.png' },
      { productName: 'Item 4', price: 49.99, imageUrl: '/media/product1.png' },
      { productName: 'Item 5', price: 59.99, imageUrl: '/media/product1.png' },
      { productName: 'Item 6', price: 69.99, imageUrl: '/media/product1.png' },
      { productName: 'Item 7', price: 79.99, imageUrl: '/media/product1.png' },
    ];

    const [searchInput, setSearchInput] = useState('');
    const [searchCategory, setSearchCategory] = useState('All');

    const handleSearchInputChange = (event) => {
      setSearchInput(event.target.value);
    };
  
    const handleSearchCategoryChange = (event) => {
      setSearchCategory(event.target.value);
    };
  
    return (
      <div>
        <Appbar isLoggedIn={isLoggedIn} userData={userData}/>
        <Grid 
          container 
          spacing={2}
          sx={{ 
            marginTop: '32px', 
            marginLeft: 2, 
            marginRight: 2,
            marginBottom: 2,
          }}
        >
          <Grid item>
            <Typography variant="h6">Search by name:</Typography>
          </Grid>
          <Grid item>
            <TextField 
              value={searchInput}
              id="outlined-search" 
              label="Search field" 
              type="search" 
              size="small"
              onChange={handleSearchInputChange}
            />
          </Grid>
          <Grid item>
            <select value={searchCategory} onChange={handleSearchInputChange}>
                <option value="">Select category</option>
                <option value="All">All</option>
                <option value="Category 1">Category 1</option>
                <option value="Category 2">Category 2</option>
            </select>
          </Grid>
          <Grid item>
            <Button variant="contained">Search</Button>
          </Grid>
        </Grid>
        <Grid 
          container 
          spacing={3}
          sx={{ 
            marginTop: '32px', 
            marginLeft: 2, 
            marginRight: 2,
            marginBottom: 2 
          }}
        >
          {products.map((product, index) => (
            <Grid item key={index}>
              <Product product={product} />
            </Grid>
          ))}
        </Grid>
      </div> 
    );
  }
