import React from 'react';
import { Card, CardContent, CardActions, TextField, Button, Typography } from '@mui/material';
import Grid from '@mui/material/Grid2';
import { useNavigate } from 'react-router-dom';
import { useState } from 'react';



export default function Login(){
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({
          ...formData,
          [name]: value
        });
    };

    const navigate = useNavigate();

    const handleLogin = (event) => {
        if (formData.username === 'kelly' && formData.password === '123456') {
            navigate('/home');
          } else {
            alert('Invalid username or password!');
            navigate('/login'); 
          }
    };

    return (
        <Grid container justifyContent="center" alignItems="center" style={{ height: '100vh', backgroundColor: '#1976d2'}}>
        <Card sx={{ maxWidth: 400, padding: 2 }}>
            <Typography variant="h5" component="div" gutterBottom align="center">
            Login
            </Typography>
            <CardContent>
            <form onSubmit={handleLogin}>
                <TextField
                fullWidth
                margin="normal"
                label="Username"
                variant="outlined"
                required
                value={formData.username} 
                name="username"
                onChange={handleChange}
                />
                <TextField
                fullWidth
                margin="normal"
                label="Password"
                type="password"
                variant="outlined"
                required
                value={formData.password} 
                name="password"
                onChange={handleChange}
                />
                <CardActions sx={{ justifyContent: 'center' }}>
                <Button type="submit" variant="contained" color="primary">
                    Login
                </Button>
                </CardActions>
            </form>
            </CardContent>
        </Card>
        </Grid>
    );
}
