import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { Card, CardContent, Typography, Button, IconButton, TextField, Dialog, DialogActions, DialogContent, DialogTitle } from '@mui/material';
import Grid from '@mui/material/Grid2';
import Appbar from './Appbar.js';
import { useState } from 'react';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit'; // Import EditIcon
import AddIcon from '@mui/icons-material/Add';

export default function Profile() {
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const userData = {
    shoppingCartItems: 3,
    username: "JohnDoe",
    email: "john.doe@example.com",
    contactNumber: "+123456789",
  };

  const [shippingAddresses, setShippingAddresses] = useState([
    '123 Main St, Cityville',
    '456 Oak St, Townsville',
    '789 Maple St, Villagetown'
  ]);

  const [dialogOpen, setDialogOpen] = useState(false);
  const [currentAddress, setCurrentAddress] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editingIndex, setEditingIndex] = useState(null);

  // Function to handle adding a new address
  const handleAddAddress = () => {
    setCurrentAddress('');
    setIsEditing(false);
    setDialogOpen(true);
  };

  // Function to open the dialog for editing an existing address
  const handleEditAddress = (index) => {
    setCurrentAddress(shippingAddresses[index]);
    setEditingIndex(index);
    setIsEditing(true);
    setDialogOpen(true);
  };

  // Function to delete a shipping address
  const handleDeleteAddress = (index) => {
    const updatedAddresses = shippingAddresses.filter((_, i) => i !== index);
    setShippingAddresses(updatedAddresses);
  };

  // Function to handle saving or editing the address
  const handleSaveAddress = () => {
    if (isEditing) {
      // Update the existing address
      const updatedAddresses = shippingAddresses.map((address, index) => 
        index === editingIndex ? currentAddress : address
      );
      setShippingAddresses(updatedAddresses);
    } else {
      // Add the new address
      setShippingAddresses([...shippingAddresses, currentAddress]);
    }
    setDialogOpen(false);
  };

  return (
    <div>
      <Appbar isLoggedIn={isLoggedIn} userData={userData} />
      <Grid container justifyContent="center" alignItems="flex-start" spacing={1} style={{ backgroundColor: '#ededed'}}>

        {/* Profile Card */}
        <Card sx={{ padding: 2, marginTop: '32px', width: '80%', marginBottom: '32px'}}>
          <CardContent>
            <Typography gutterBottom variant="h6" component="div">
              Profile
            </Typography>
            <Typography variant="body1" sx={{marginTop:'5px'}}>
              <strong>Username:</strong> {userData.username}
            </Typography>
            <Typography variant="body1" sx={{marginTop:'5px'}}>
              <strong>Email:</strong> {userData.email}
            </Typography>
            <Typography variant="body1" sx={{marginTop:'5px'}}>
              <strong>Contact Number:</strong> {userData.contactNumber}
            </Typography>
            <Button variant="outlined" startIcon={<EditIcon />} sx={{ marginTop: '16px' }}>
              Edit
            </Button>
          </CardContent>
        </Card>

        {/* Shipping Address Card */}
        <Card sx={{ padding: 2, width: '80%', marginBottom: '32px'}}>
          <CardContent>
            <Typography gutterBottom variant="h6" component="div">
              Shipping Address
            </Typography>
            <TableContainer component={Paper}>
              <Table aria-label="shipping address table">
                <TableHead>
                  <TableRow>
                    <TableCell>Number</TableCell>
                    <TableCell>Shipping Address</TableCell>
                    <TableCell align="right">Actions</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {shippingAddresses.map((address, index) => (
                    <TableRow key={index}>
                      <TableCell>{index + 1}</TableCell>
                      <TableCell>{address}</TableCell>
                      <TableCell align="right">
                        <Button variant="outlined" color="primary" onClick={() => handleEditAddress(index)}>
                          <EditIcon /> {/* Updated to use EditIcon */}
                          Edit
                        </Button>
                        <IconButton aria-label="delete" color="error" onClick={() => handleDeleteAddress(index)}>
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            <Button variant="contained" color="primary" sx={{ marginTop: 2 }} onClick={handleAddAddress}>
              <AddIcon /> {/* Optional: You can also use an icon here for adding addresses */}
              Add New Shipping Address
            </Button>
          </CardContent>
        </Card>
      </Grid>

      {/* Dialog for adding/editing address */}
      <Dialog open={dialogOpen} onClose={() => setDialogOpen(false)}>
        <DialogTitle>{isEditing ? "Edit Shipping Address" : "Add New Shipping Address"}</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            label="Shipping Address"
            fullWidth
            value={currentAddress}
            onChange={(e) => setCurrentAddress(e.target.value)}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setDialogOpen(false)} color="primary">Cancel</Button>
          <Button onClick={handleSaveAddress} color="primary">
            {isEditing ? "Save Changes" : "Add Address"}
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
