import * as React from 'react';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Paper from '@mui/material/Paper';
import { Card, CardContent, Typography, Button, IconButton, Select, MenuItem, InputLabel, FormControl, TextField } from '@mui/material';
import Grid from '@mui/material/Grid2';
import Appbar from './Appbar.js';
import { useState } from 'react';
import AddIcon from '@mui/icons-material/Add';  // Import your icons here
import RemoveIcon from '@mui/icons-material/Remove'; // Import your icons here
import DeleteIcon from '@mui/icons-material/Delete'; // Import your icons here

function createData(name, price, quantity) {
  return { name, price, quantity };
}

const initialRows = [
  createData('Product 1', 10.0, 1),
  createData('Product 2', 20.0, 2),
  createData('Product 3', 30.0, 1),
  createData('Product 4', 40.0, 3),
  createData('Product 5', 50.0, 1),
];

export default function BasicTable() {
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const userData = {
    shoppingCartItems: 3,
  };

  const [rows, setRows] = useState(initialRows);
  const [paymentMethod, setPaymentMethod] = useState('');
  const [address, setAddress] = useState('');

  // Calculate total price
  const totalPrice = rows.reduce((sum, row) => sum + row.price * row.quantity, 0);

  const increaseQuantity = (index) => {
    const newRows = [...rows];
    newRows[index].quantity += 1;
    setRows(newRows);
  };

  const decreaseQuantity = (index) => {
    const newRows = [...rows];
    if (newRows[index].quantity > 1) {
      newRows[index].quantity -= 1;
      setRows(newRows);
    }
  };

  const deleteItem = (index) => {
    const newRows = rows.filter((_, i) => i !== index);
    setRows(newRows);
  };

  const handlePaymentChange = (event) => {
    setPaymentMethod(event.target.value);
  };

  const handleAddressChange = (event) => {
    setAddress(event.target.value);
  };

  return (
    <div>
      <Appbar isLoggedIn={isLoggedIn} userData={userData} />
      <Grid container justifyContent="center" alignItems="flex-start" spacing={2} style={{ backgroundColor: '#ededed'}}>
        
        {/* Left card (Shopping Cart) */}
        <Grid item xs={8}> {/* 2/3 of the width */}
          <Card sx={{ padding: 2 }}>
            <CardContent>
              <Typography variant='h6'>Shopping Cart</Typography>
              <TableContainer component={Paper}>
                <Table sx={{ minWidth: 650 }} aria-label="simple table">
                  <TableHead>
                    <TableRow>
                      <TableCell>Product Name</TableCell>
                      <TableCell align="right">Product Price ($)</TableCell>
                      <TableCell align="right">Product Quantity</TableCell>
                      <TableCell align="right">Delete</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {rows.map((row, index) => (
                      <TableRow key={row.name}>
                        <TableCell component="th" scope="row">
                          {row.name}
                        </TableCell>
                        <TableCell align="right">{row.price.toFixed(2)}</TableCell>
                        <TableCell align="right">
                          <IconButton onClick={() => decreaseQuantity(index)}>
                            <RemoveIcon />
                          </IconButton>
                          {row.quantity}
                          <IconButton onClick={() => increaseQuantity(index)}>
                            <AddIcon />
                          </IconButton>
                        </TableCell>
                        <TableCell align="right">
                          <IconButton onClick={() => deleteItem(index)}>
                            <DeleteIcon />
                          </IconButton>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </CardContent>
          </Card>
        </Grid>

        {/* Right card (Payment and Checkout) */}
        <Grid item xs={4}> {/* 1/3 of the width */}
          <Card sx={{ padding: 2 }}>
            <CardContent>
              <Typography variant='h6'>Order Summary</Typography>
              <Typography variant="body1" sx={{ marginBottom: 2 }}>
                Total: ${totalPrice.toFixed(2)}
              </Typography>

              {/* Payment Method */}
              <FormControl fullWidth sx={{ marginBottom: 2 }}>
                <InputLabel id="payment-method-label">Payment Method</InputLabel>
                <Select
                  labelId="payment-method-label"
                  id="payment-method"
                  value={paymentMethod}
                  onChange={handlePaymentChange}
                >
                  <MenuItem value="credit-card">Credit Card</MenuItem>
                  <MenuItem value="debit-card">Debit Card</MenuItem>
                  <MenuItem value="payNow">PayNow</MenuItem>
                </Select>
              </FormControl>

              {/* Address Selection */}
              <TextField
                id="address"
                label="Shipping Address"
                variant="outlined"
                fullWidth
                value={address}
                onChange={handleAddressChange}
                sx={{ marginBottom: 2 }}
              />

              <Button variant="contained" fullWidth>
                Checkout
              </Button>
            </CardContent>
          </Card>
        </Grid>

      </Grid>
    </div>
  );
}
